import React, { useEffect, useState } from 'react';
import { SendBirdProvider, Channel, ChannelList } from '@sendbird/uikit-react';
import '@sendbird/uikit-react/dist/index.css';
import './App.css';
import ChatComponent from './ChatComponent';
import { createGroupChannel } from './utils';

function App() {
  const [channelUrl, setChannelUrl] = useState('');
  const [showLeftPanel, setShowLeftPanel] = useState(false);

  useEffect(() => {
    const initializeChannel = async () => {
      const url = await createGroupChannel();
      setChannelUrl(url);
    };

    initializeChannel();

    // Add touch event listeners for swipe
    const handleTouchStart = (e) => {
      this.touchStartX = e.touches[0].clientX;
    };

    const handleTouchMove = (e) => {
      if (!this.touchStartX) return;
      const touchEndX = e.touches[0].clientX;
      if (this.touchStartX - touchEndX > 50) {
        // Swipe left
        setShowLeftPanel(false);
      }
      if (this.touchStartX - touchEndX < -50) {
        // Swipe right
        setShowLeftPanel(true);
      }
      this.touchStartX = null;
    };

    window.addEventListener('touchstart', handleTouchStart);
    window.addEventListener('touchmove', handleTouchMove);

    return () => {
      window.removeEventListener('touchstart', handleTouchStart);
      window.removeEventListener('touchmove', handleTouchMove);
    };
  }, []);

  return (
    <SendBirdProvider appId={process.env.REACT_APP_SENDBIRD_APP_ID} userId="guest_user" nickname="Guest">
      <div className="app">
        <div className={`left-panel ${showLeftPanel ? 'show' : ''}`}>
          <ChannelList />
        </div>
        <div className="right-panel">
          {channelUrl && <Channel channelUrl={channelUrl} />}
          <ChatComponent channelUrl={channelUrl} />
        </div>
        <button className="toggle-button" onClick={() => setShowLeftPanel(!showLeftPanel)}>
          {showLeftPanel ? '닫기' : '열기'}
        </button>
      </div>
    </SendBirdProvider>
  );
}

export default App;
